import React, { Component } from "react";
import "./App.css";
import Clock from "./Clock";

class App extends Component {
  state = {
    secondRatio: 0,
    minuteRatio: 0,
    hourRatio: 0,
  };

  componentDidMount() {
    setInterval(() => {
      this.setClock();
    }, 1000);
  }

  setClock = () => {
    const currentDate = new Date();
    const secondRatio = currentDate.getSeconds() / 60;
    const minuteRatio = (secondRatio + currentDate.getMinutes()) / 60;
    const hourRatio = (minuteRatio + currentDate.getHours()) / 12;

    this.setState({ secondRatio: secondRatio });
    this.setState({ minuteRatio: minuteRatio });
    this.setState({ hourRatio: hourRatio });
  };

  render() {
  const {secondRatio , minuteRatio , hourRatio} = this.state
    return <Clock secondRatio={secondRatio} minuteRatio={minuteRatio} hourRatio={hourRatio} />;
  }
}

export default App;
